#!/bin/bash
echo "Begin installing ROS Hydro for Debian"; sudo sh -c 'echo "deb http://packages.ros.org/ros/ubuntu wheezy main" > /etc/apt/sources.list.d/ros-latest.list' && wget https://raw.githubusercontent.com/ros/rosdistro/master/ros.key -O - | sudo apt-key add - && sudo apt-get -y update && echo "0.1. Installing bootstrap dependencies..."; sudo apt-get -y install python-rosdep python-rosinstall-generator && sudo pip install -U -q rosdep rosinstall_generator wstool rosinstall && echo "1. Create a Catkin Workspace"; sudo rosdep init && rosdep update && mkdir ~/ros_catkin_ws && cd ~/ros_catkin_ws && rosinstall_generator ros_comm --rosdistro hydro --deps --wet-only --tar > hydro-ros_comm-wet.rosinstall && wstool init -j8 src hydro-ros_comm-wet.rosinstall && wstool update -j 4 -t src && echo "2. Resolving Dependencies..."; rosdep install --from-paths src --ignore-src --rosdistro hydro -y -r --os=debian:wheezy && echo "3. Dependencies not available in the Debian Repo"; mkdir ~/ros_catkin_ws/external_src && sudo apt-get install -y checkinstall && echo "Skipped 4.\n5. Building Ros" cd ~/ros_catkin_ws && ./src/catkin/bin/catkin_make_isolated --install -DCMAKE_BUILD_TYPE=Release && echo "source ~/ros_catkin_ws/install_isolated/setup.bash" >> ~/.bashrc && echo "Finished"
